import React, { Component } from 'react';
import { Redirect } from "react-router-dom"
import axios from "axios"

import SideMenu from "../components/userHome/sideMenu";
import Server from "../components/userHome/server"
import Options from "../components/userHome/options"

import CheckUserAuth from "../auth/checkAuth"

import { store } from "../index"
import { fetchUserData, checkUserAuth } from "../redux/actions/index"

class UserHomePage extends Component {
    constructor (props){
        super(props)
        this.state = { 
            page: ["server"],
            userData: [store.getState()]
        }
    }

    componentWillMount(){        
        // store.dispatch(fetchUserData)
        // store.dispatch(checkUserAuth)
        // this.setState({
        //     userData: store.getState()
        // }) 


        const cookieValue = document.cookie
        .split('; ')
        .find(row => row.startsWith('loginAuth='))
        .split('=')[1];

        axios.get("http://192.168.1.247:3001/api/user/auth", { 
            withCredentials: true,
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
                "Authorization": cookieValue
            }
        })
        .then(response=>{
            console.log("req success!")
            this.setState({
                userPage: true
            })
        })


        console.log("1",store.getState())
    }

    componentDidUpdate(prevProps, prevState, snapshot){
        console.log("component update")
    }

    changeState = (data)=>{
        this.setState({
            page:data
        })
     }

    getPage = ()=>{
         let component
         switch (this.state.page){
            case "server":
                component = <Server userData={this.state.userData} />
                break;
            case "options":
                component = <Options userData={this.state.userData} />
                break;
            default:
                component = <Server userData={this.state.userData} />
                break;
            }
        return component
    }

    authSuccess = ()=>{
        return(
            <dir className="userHomeLayout">
                <dir className="userHomesideMenu">
                    <SideMenu setState={this.changeState}/>
                </dir>
                {this.getPage()}
            </dir> 
        )
    }

    authFaliure = ()=>{
        return(
            <Redirect to="/user/login"/>
        )
    }

    render() { 
        console.log("userdata: ", this.state.userData)
        switch (this.state.userData.auth) {
            case true:
                return this.authSuccess()
            default:
                return this.authFaliure()
        }
    }
}

export default UserHomePage;


