import React, { Component } from 'react';

class FloatingAniamtion extends Component {
    constructor(props){
        super(props)

        this.state = { 
            x:0, 
            y:0, 
            xAxis: 0,
            yAxis: 0 
        }
    }

    mouseMoveHandler(e){
        this.setState({x:e.screenX, y:e.screenY})
        this.axis()
    } 

    axis(){
        const container = document.querySelector(".floatOuterContainer")
        this.setState({xAxis:(this.state.x - container.offsetWidth /2), 
            yAxis:(container.offsetHeight /2 - this.state.y)});
        this.animation()
    }

    animation(){
        const object = document.querySelector(".floatInnerContainer")
        object.style.transform = `rotateY(${xAxis}deg) rotateX(${yAxis}deg)`
    }   

    render() { 
        const { x,y,xAxis,yAxis } = this.state
        return ( 
        <div className="floatOuterContainer" onMouseMove={ this.mouseMoveHandler.bind(this) }>
            <div className="floatInnerContainer">
                <h1>mouse coordinates: { x }, { y }</h1>
                <h1>axises: {xAxis}, {yAxis}</h1>
            </div>
        </div>
         );
    }
}

export default FloatingAniamtion;
