import React,{ Component } from "react"
import axios from "axios"

class SignupPage extends Component {

    state = {
        name: "",
        email: "",
        password: ""
    }

    handleNameChange = (e)=>{
        this.setState({name: e.target.value})
        console.log(this.state.name)
    }

    handleEmailChange = (e)=>{
        this.setState({email: e.target.value})
        console.log(this.state.email)
    }

    handlePasswordChange = (e)=>{
        this.setState({password: e.target.value})
        console.log(this.state.password)
    }

    displayValues = ()=>{
        console.log("Name: ", this.state.name)
        console.log("Email: ", this.state.email)
        console.log("Password: ", this.state.password)
    }

    postReq = ()=>{
        axios.post("/user/insert",{
            data:{
                name: "Nils",
                email: "nils@u1.se",
                password: "NilsCoool"
            }
        })
    }

    render(){
        return ( 
            <div className="loginMainBody">
                <div className="SignupBody">
                    <div className="signUpContainerHeader">
                        <span>SIGN UP</span>
                    </div>
                    <form action="login" method="POST" className="loginCenterInnerContainer">

                        <div className="loginForm">
                            <input type="text" name="name" className="loginInput" value={this.state.name} onChange={this.handleNameChange} autoComplete="off" required/>
                            <label className="loginLable">
                                <span className="loginLableValue">Name</span>
                            </label>
                        </div>
                        <div className="loginForm">
                            <input  type="text" name="email" className="loginInput" value={this.state.email} onChange={this.handleEmailChange} autoComplete="off" required/>
                            <label className="loginLable">
                                <span className="loginLableValue">Email</span>
                            </label>
                        </div>
                        <div className="loginForm">
                            <input type="password" name="password" className="loginInput" value={this.state.password} onChange={this.handlePasswordChange} autoComplete="off" required/>
                            <label className="loginLable">
                                <span className="loginLableValue">Password</span>
                            </label>
                        </div>    
                        <button type="submit" className="loginButton" onClick={this-displayValues}>submit</button>
                    </form>
                </div>
            </div>
            );
    }

}

export default SignupPage;
