import { useState, useEffect } from "react"

const LogsComponent = (props) => {
    const refreshData = props.refreshData
    const [userData, setUserData] = useState(props.userData)
    const [logsExpand, setLogsExpand] = useState(false)

    useEffect(() => {
        // logic for logs interaction
        // making sure the interaction logic on fire when logs is active
        if (userData.serverPods.status === "True") {
            const ele = document.getElementById('logsText');

            console.log("ele: ", document)

            ele.style.cursor = 'grab';
            ele.scrollTop = ele.scrollHeight;

            let pos = { top: 0, left: 0, x: 0, y: 0 };

            const mouseDownHandler = function (e) {
                ele.style.cursor = 'grabbing';
                ele.style.userSelect = 'none';

                pos = {
                    left: ele.scrollLeft,
                    top: ele.scrollTop,
                    // Get the current mouse position
                    x: e.clientX,
                    y: e.clientY,
                };

                document.addEventListener('mousemove', mouseMoveHandler);
                document.addEventListener('mouseup', mouseUpHandler);
            };

            const mouseMoveHandler = function (e) {
                // How far the mouse has been moved
                const dx = e.clientX - pos.x;
                const dy = e.clientY - pos.y;

                // Scroll the element
                ele.scrollTop = pos.top - dy;
                ele.scrollLeft = pos.left - dx;
            };

            const mouseUpHandler = function () {
                ele.style.cursor = 'grab';
                ele.style.removeProperty('user-select');

                document.removeEventListener('mousemove', mouseMoveHandler);
                document.removeEventListener('mouseup', mouseUpHandler);
            };

            // Attach the handler
            ele.addEventListener('mousedown', mouseDownHandler);
        }
    }, []);

    const expandLogs = () => {
        setLogsExpand(!logsExpand)
        console.log("This is the logsExpand function; ", logsExpand)
    }


    // ------------- functions for different pods status -----------


    const running = () => {
        return (
            <div className={logsExpand ? "logsMainContainer logsMainContainerHover logsMainContainerExpanded userHomeSegment" : "logsMainContainer userHomeSegment"}>
                <div className="logsExpandArrow fas fa-expand-arrows-alt" onClick={expandLogs} data-tip data-for="expandLogs" />
                <div className="logsText" id="logsText">
                    <span>
                        {userData.serverPods.logs}
                        <p></p>
                    </span>
                </div>
                <div className="logsTextContainer">
                    <span>Logs</span>
                    <div onClick={refreshData}>
                        <div className="checkStatus fas fa-sync" id="checkStatus" data-tip data-for="refreshInfo" />
                    </div>
                </div>
            </div>
        )
    }

    const logsMessageBoiler = () => {
        return (
            <div className="logsMainContainer userHomeSegment">
                <div className="logsTextContainer">
                    <span>Logs</span>
                </div>
            </div>
        )
    }

    const shuttingDownStartingUp = () => {
        return (
            <div className="logsMainContainer userHomeSegment">
                <div className="logsTextContainer">
                    <span>Logs</span>
                </div>
            </div>
        )
    }

    const queue = () => {
        return (
            <div className="logsMainContainer userHomeSegment">
                <div className="logsTextContainer">
                    <span>Logs</span>
                </div>
            </div>
        )
    }

    const notRunnig = () => {
        return (
            <div className="logsMainContainer userHomeSegment">
                <div className="logsTextContainer">
                    <span>Logs</span>
                </div>
            </div>
        )
    }

    // ---------------------------------------------------------------------

    // what is displayed in the logs box
    switch (userData.serverPods.status) {
        case "True":
            return running()
        case "False":
            return logsMessageBoiler("shuttingDown")
        case "Pending":
            return logsMessageBoiler("startingUp")
        case "Queuing":
            return logsMessageBoiler("queue")
        case "server not running":
            return logsMessageBoiler("notRunning")
        default:
            return running()
    }
}

export default LogsComponent
