const initailState = {
    loading:false,
    user: []
}

const reducer = (state = initailState,action)=>{

    switch (action.type) {
        case FETCH_USER_LOADING:
            return{
                ...state,
                loading: true
            }
        case FETCH_USER_SUCCESS:
            return{
                ...state,
                user: action.payload
            }
        default:
            return state;
    }
}

export default reducer
