import React, { Component } from 'react';

class FloatingAniamtion extends Component {
    constructor(props){
        super(props)

        this.state = { x:0, y:0 }
    }

    mouseMoveHandler(e){
        this.setState({x:e.screenX, y:e.screenY})
    } 

    render() { 
        const { x,y } = this.state
        return ( 
        <div className="floatOuterContainer" onMouseMove={ this.mouseMoveHandler }>
            <div className="floatInnerContainer">
                <h1>mouse coordinates: { x }{ y }</h1>
            </div>
        </div>
         );
    }
}

export default FloatingAniamtion;
