import React, { Component } from 'react';
import { Redirect } from "react-router-dom"
import axios from "axios"

import SideMenu from "../components/userHome/sideMenu";
import Server from "../components/userHome/server"
import Options from "../components/userHome/options"

import CheckUserAuth from "../auth/checkAuth"

import { store } from "../index"
import { fetchUserData, checkUserAuth } from "../redux/actions/index"

class UserHomePage extends Component {
    constructor (props){
        super(props)
        this.state = { 
            page: ["server"],
            userData: [store.getState()]
        }
    }

    componentWillMount(){        
        store.dispatch(fetchUserData)
        store.dispatch(checkUserAuth)
        this.setState({
            userData: store.getState()
        }) 
        console.log("1",store.getState())
    }

    changeState = (data)=>{
        this.setState({
            page:data
        })
     }

    getPage = ()=>{
         let component
         switch (this.state.page){
            case "server":
                component = <Server userData={this.state.userData} />
                break;
            case "options":
                component = <Options userData={this.state.userData} />
                break;
            default:
                component = <Server userData={this.state.userData} />
                break;
            }
        return component
    }

    authSuccess = ()=>{
        return(
            <dir className="userHomeLayout">
                <dir className="userHomesideMenu">
                    <SideMenu setState={this.changeState}/>
                </dir>
                {this.getPage()}
            </dir> 
        )
    }

    authFaliure = ()=>{
        return(
            <Redirect to="/user/login"/>
        )
    }

    render() { 
        console.log("userdata: ", this.state.userData[0].auth)
        switch (this.state.userData.auth) {
            case true:
                return this.authSuccess()
            default:
                return this.authFaliure()
        }
    }
}

export default UserHomePage;


