import { FETCH_USER_LOADING, FETCH_USER_SUCCESS } from "../types"

const initailState = {
    user: []
}

const reducer = (state = initailState,action)=>{

    switch (action.type) {
        case FETCH_USER_LOADING:
            return{
                ...state,
                user: "loading"
            }
        case FETCH_USER_SUCCESS:
            return{
                user: action.payload
            }
        default:
            break;
    }
}

export default reducer
