import { combineReducers } from "redux"

const authSuccess = (state = false,action)=>{
    switch (action.type) {
        case "AUTH_SUCCESS":
            return true
        default:
            return false
    }
}


const fetchUser = (action)=>{

    switch (action.type) {
        case "FETCH_USER_SUCCESS":
            return{
                user: action.payload
            }
        default:
            return{
                user: "testDefault"
            }
    }
}

const rootReducer = combineReducers({
    fetchUser,
    authSuccess
})

export default rootReducer
