import { combineReducers } from "redux"

const initailState = {
    user: "default",
    auth: null
}

const reducer = (state = initailState, action) => {

    switch (action.type) {
        case "FETCH_USER_SUCCESS":
            return {
                ...state,
                user: action.payload
            }
        case "AUTH_SUCCESS":
            return {
                ...state,
                auth: action.payload
            }
        case "CREATE_MC_CONF":
            return {
                ...state,
                auth: action.payload
            }
        default:
            return {
                ...state
            }
    }
}

export default reducer
