import axios from "axios"
import { useCookies } from "react-cookie"

export const fetchUserData = async(dispatch, getState)=>{
    const response = await axios.get("http://192.168.1.247:3001/api/user", {
        headers: {
            //"customHeader": "test"
        }
    })
    .then(res=>{
        const user = res.data
        dispatch({
            type: "FETCH_USER_SUCCESS",
            payload: res.data
        })
    })
}

export const checkUserAuth = async(dispatch)=>{
    const cookieValue = document.cookie
    .split('; ')
    .find(row => row.startsWith('loginAuth='))
    .split('=')[1];

    await axios.get("http://192.168.1.247:3001/api/user/auth", { 
        withCredentials: true,
        headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json',
            "Authorization": cookieValue
        }
    })
    .then(response=>{
        console.log("responsedataauth: ",response.data.auth)
        dispatch({
            type: "AUTH_SUCCESS"
        })
    })
}

export const authSucess = (dispatch)=>{
    dispatch({
        type: "AUTH_SUCCESS"
    })
}
