import React, { Component } from 'react';

class FloatingAniamtion extends Component {
    constructor(props){
        super(props)

        this.state = { x:0, y:0 }
    }

    mouseMoveHandler = (e)=>{
        this.setState({x:e.screenX, y:e.screenY})
    } 

    render() { 
        return ( 
        <div className="floatOuterContainer" onMouseMove={ mouseMoveHandler }>
            <div className="floatInnerContainer">

            </div>
        </div>
         );
    }
}

export default FloatingAniamtion;
