import React, { useEffect, useState } from "react";
import { useHistory } from "react-router-dom";
import { store } from "../index"
import { signup } from "../redux/actions/index"

const SignupPage = () => {

  const history = useHistory();

  const [toggle, setToggle] = useState(true)

  useEffect(() => {
    if (store.getState().auth) {
      history.push("/user/home");
      window.location.reload();
    }
  }, [])

  const postReq = (e) => {
    e.preventDefault();
    signup(e.target.name.value, e.target.email.value, e.target.password.value, store.dispatch)
  };

  const changeToggle = () => {
    setToggle(!toggle)
  }

  const toLogin = () => {
    history.push("/user/login");
    window.location.reload();
  }

  const inputPage = () => {
    return (
      <div className="signupMainBody">
        <div className="SignupBody">
          <div className="signUpContainerHeader">
            <span>SIGN UP</span>
          </div>
          <form onSubmit={postReq} className="loginCenterInnerContainer">
            <div className="signupForm signupFormName">
              <input
                type="text"
                name="name"
                className="loginInput"
                autoComplete="off"
                required
              />
              <label className="loginLable">
                <span className="loginLableValue">Name</span>
              </label>
            </div>
            <div className="signupForm signupFormEmail">
              <input
                type="text"
                name="email"
                className="loginInput"
                autoComplete="off"
                required
              />
              <label className="loginLable">
                <span className="loginLableValue">Email</span>
              </label>
            </div>
            <div className="signupForm signupFormPassword">
              <input
                type="password"
                name="password"
                className="loginInput"
                autoComplete="off"
                required
              />
              <label className="loginLable">
                <span className="loginLableValue">Password</span>
              </label>
            </div>
            <button type="submit" className="signupForm signupButton" onClick={changeToggle}>
              submit
            </button>
            <span onClick={toLogin}>Already signed up?</span>
          </form>
        </div>
      </div>
    );
  }

  const messagePage = () => {
    return (
      <div className="signupMainBody">
        <div className="SignupBody">
          <div className="messageBody">
            <span>Check you mail to verify you account!</span>
            <div>
              <span>Haven't gotten an email?</span>
              <span>Check you spam folder or try and send another one!</span>
            </div>
          </div>
        </div>
      </div>
    )
  }

  switch (toggle) {
    case true:
      return messagePage();
    default:
      return inputPage();
  }

};

export default SignupPage;
