import axios from "axios"
const getEmail = async(state = "none",action)=>{

    state = await axios.get("http://192.168.1.247:3001/api/user/home", {
        headers: {
            //"customHeader": "test"
        }
    })
    .then(res=>{
        return res.data.data
    })

    return state
}

export default getEmail
