// modules
import { useState, useEffect } from "react"
import { useHistory } from "react-router-dom";

// redux
import { store } from "../../index"

// custom files
import { passFormPayment, failPayment } from "./paymentHandler"
import { OneTimePayment } from "./payments"

const ServerPlan = (props) => {
    // const [passForm, setpassForm] = useState("default")
    const [passForm, setpassForm] = useState("default")

    // redux
    const [userData, setUserData] = useState(store.getState())

    store.subscribe(() => {
        setUserData(store.getState());
    });

    // history
    const history = useHistory();

    // enable disable scroll paymentHandler
    useEffect(() => {
        if (passForm != "default") {
            document.body.style.overflow = "hidden"
        } else if (passForm === "default") {
            document.body.style.overflow = "scroll"
        }
    }, [passForm])

    const paymentSelection = (value) => {
        switch (value) {
            case "sub":
                return subscriptionPayment();
            case "oneTime":
                return OneTimePayment();
            default:
                return OneTimePayment();
        }
    }

    // --------------------------------------- loginSignupFunctions ------------------------------------------

    const changeLoginSignupHandler = (showSignup) => {
        console.log('%c%s', 'color: green', showSignup)
        PaymentLoginSignupBoiler(showSignup)
    }

    const PaymentLogin = () => {
        return (
            <>
                <span className="paymentLoginLoginHeader">LOGIN</span>
                <div className="paymentLoginSignupsHeaderSeperator" />
                <input className="paymentLoginEmail" type="text" placeholder="Email" />
                <input className="paymentLoginPassword" type="text" placeholder="Password" />
                <div className="paymentLoginButtonLinkContainer">
                    <button>Login</button>
                    <span onClick={() => { changeLoginSignupHandler(true) }}>Don't have an account? Sign up</span>
                </div>
            </>
        )

    }

    const PaymentSignup = () => {
        return (
            <>
                <span className="paymentLoginLoginHeader">SIGNUP</span>
                <div className="paymentLoginSignupsHeaderSeperator" />
                <input className="paymentLoginEmail" type="text" placeholder="Email" />
                <input className="paymentLoginPassword" type="text" placeholder="Password" />
                <div className="paymentLoginButtonLinkContainer">
                    <button>Login</button>
                    <span onClick={() => { changeLoginSignupHandler(false) }}>Don't have an account? Sign up</span>
                </div>
            </>
        )
    }

    const PaymentLoginSignupBoiler = (showSignup) => {
        console.log('%c%s', 'color: red', showSignup)
        return (
            <div className="paymentLoginSignupMainContainer">
                {
                    showSignup ? <PaymentSignup /> : <PaymentLogin />
                }
            </div>
        )
    }

    // -----------------------------------------------------------------------------------------------------------------------

    const paymentpassForm = () => {

    }

    const paymentFailure = () => {

    }

    const subscriptionPayment = () => {

    }

    const isLoggedInFunc = () => {
        if (userData.auth) {
            return (
                OneTimePayment(props)
            )
        }
        return (
            //OneTimePayment(props)
            PaymentLoginSignupBoiler(false)
        )
    }

    const paymentHandler = () => {
        switch (passForm) {
            case "passForm":
                return passFormPayment(history);
            case "fail":
                return failPayment();
            case "default":
                return null;
            default:
                return null;
        }
    }

    return (
        <>
            <div className="paymentInfoContainer">
                <div className="paymentInnerHeader">
                    <span>Info</span>
                    <div className="paymentInnerHeaderSeperator" />
                </div>
                <div className={props.values.plan + " paymentInfoPriceContainer"}>
                    <span className="paymentInfoPlan">{props.values.plan}</span>
                    <span className="paymentInfoPrice">{props.values.price}€</span>
                </div>
            </div>
            <div className="paymentDataContainer">
                <div className="paymentInnerHeader">
                    <span>Pay</span>
                    <div className="paymentInnerHeaderSeperator" />
                </div>
                {isLoggedInFunc()}
                {/* INSERT A PROPER SOLUTION TO CHOSE BETWEEN ONE TIME PAYMENT AND SUBSCRIPTION */}
                {/* <div className="paymentInnerPaymentSelectionMain">
                </div> */}
            </div>
            {paymentHandler()}
        </>
    )
}

export default ServerPlan
