import { combineReducers } from "redux"

const initailState = {
    user: "default",
    auth: false
}

const fetchUser = (state = initailState,action)=>{

    switch (action.type) {
        case "FETCH_USER_SUCCESS":
            return{
                ...state,
                user: action.payload
            }
        case "AUTH_SUCCESS":
            return{
                ...state,
                auth: true
            }
        default:
            return{
                ...state
            }
    }
}

const rootReducer = combineReducers({
    fetchUser,
    authSuccess
})

export default rootReducer
