import { useHistory } from "react-router-dom";
import { useState } from "react";

import MinecraftBasic from "./basic";

const MinecraftCreate = () => {

    const history = useHistory();
    const [isCardPressed, setisCardPressed] = useState("not pressed")


    if (isCardPressed !== "not pressed") {
        switch (isCardPressed) {
            case "basic":
                return (
                    <MinecraftBasic />
                )
            case "normal":
                history.push("/server/minecraft/normal");
                window.location.reload();
                break;
            case "premium":
                history.push("/server/minecraft/premium");
