import axios from "axios"

let cookieValueUserID = null
if (document.cookie && document.cookie.search("userID") > -1) {
    cookieValueUserID = document.cookie
        .split('; ')
        .find(row => row.startsWith('userID='))
        .split('=')[1];
}

let cookieValueAuth = null
if (document.cookie && document.cookie.search("loginAuth") > -1) {
    cookieValueAuth = document.cookie
        .split('; ')
        .find(row => row.startsWith('loginAuth='))
        .split('=')[1];
}

export const fetchUserData = async (dispatch, getState) => {
    await axios.post(`/user`, {
        id: cookieValueUserID,
    })
        .then(res => {
            dispatch({
                type: "FETCH_USER_SUCCESS",
                payload: res.data
            })
        })
}

export const login = async (dispatch, name, pass) => {
    await axios.post("/user/login", {
        withCredentials: true,
        name: name,
        password: pass
    }).then(async (response) => {
        if (response.data.message === "Success!") {
            await checkUserAuth()
            createMcConfig()
            dispatch({
                type: "LOGIN",
                payload: {
                    loginAuth: response.data.loginAuth,
                    userID: response.data.userID
                }
            })
        } else {
            console.log("response.data: ", response.data)
            dispatch({
                type: "ERR_MESSAGE",
                payload: response.data
            })
        }
    })
}

export const signup = async (dispatch, name, email, pass) => {
    axios.post("/user/insert", {
        data: {
            name: e.target.name.value,
            email: e.target.email.value,
            password: e.target.password.value,
        },
    })
        .then(async (response) => {
            console.log(response.data)
            if (response.data === "User created") {
                store.dispatch({
                    type: "MESSAGE",
                    payload: response.data
                })
                await store.dispatch(checkUserAuth)
                console.log("1", store.getState().auth)
                store.dispatch({
                    type: "ERR_MESSAGE",
                    payload: response.data
                })
            }
        })
    e.preventDefault();
    //history.push("/user/login");
    //window.location.reload();
}

export const checkUserAuth = async (dispatch) => {
    await axios.get(`/user/auth`, {
        withCredentials: true,
        headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json',
            "Authorization": cookieValueAuth
        }
    })
        .then(response => {
            dispatch({
                type: "AUTH_SUCCESS",
                payload: response.data.auth
            })
        })
}

export const authSucess = (dispatch) => {
    dispatch({
        type: "AUTH_SUCCESS"
    })
}

export const createMcConfig = async (dispatch) => {
    await axios.post(`/mcConf/create`, {
        id: cookieValueUserID
    })
        .then(res => {
            dispatch({
                type: "DUMP"
            })
        })
}

export const startServer = async (dispatch) => {
    await axios.post(`/server`, {
        id: cookieValueUserID,
        action: "start"
    })
        .then(res => {
            dispatch({
                type: "DUMP"
            })
        })
}

