import axios from "axios"
import { useCookies } from "react-cookie"

export const fetchUserData = async(dispatch, getState)=>{
    const response = await axios.get("http://192.168.1.247:3001/api/user", {
        headers: {
            //"customHeader": "test"
        }
    })
    .then(res=>{
        const user = res.data
        dispatch({
            type: "FETCH_USER_SUCCESS",
            payload: res.data
        })
    })
}

export const FetchUserAuth = async(dispatch, getState)=>{
    const [cookies, setCookie] = useCookies(["auth"])

    const response = await axios.get("http://192.168.1.247:3001/api/user/auth", { 
    withCredentials: true,
    headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
        "Authorization": "test"
    }
})
    .then(res=>{
        dispatch({
            type: "FETCH_USER_CHECK",
        })
    })
}

export const authSucess = (dispatch)=>{
    dispatch({
        type: "AUTH_SUCCESS"
    })
}

export const authCheck = (dispatch)=>{
    dispatch({
        type: "AUTH_CHECK"
    })
}
