const authSuccess = (state = false,action)=>{
    switch (action.type) {
        case "AUTH_SUCCESS":
            return true
        default:
            return false
    }
}

const initailState = {
    user: []
}

const fetchUser = (state = initailState,action)=>{

    switch (action.type) {
        case "FETCH_USER_SUCCESS":
            return{
                ...state,
                user: action.payload
            }
        default:
            return{
                ...state,
                user: "testDefault"
            }
    }
}

import { combineReducers } from "redux"

const rootReducer = combineReducers({
    fetchUser,
    authSuccess
})

export default rootReducer
