import axios from "axios"

export const fetchUserDataLoading = ()=>{
    return{
        type: "FETCH_USER_LOADING"
    }
}

export const fetchUserData = ()=>{
    return (dispatch) =>{
        axios.get("http://192.168.1.247:3001/api/user/home", {
            headers: {
                //"customHeader": "test"
            }
        })
        .then(res=>{
            const user = res.data
            dispatch({
                type: "FETCH_USER_SUCCESS"
            })
        })
    }
}
