import React, { Component } from 'react';

class FloatingAniamtion extends React.Component {
    constructor(props){
        super(props)

        this.state = { 
            x:0, 
            y:0, 
            xAxis: 0,
            yAxis: 0 
        }
    }

    mouseMoveHandler(e){
        this.setState({x:e.screenX, y:e.screenY})
    } 

    axis(x, y){
        const container = document.querySelector(".floatOuterContainer")
        this.xAxis = (x - container.offsetWidth)
        this.yAxis = (y - container.offsetWidth)
    }

    render() { 
        const { x,y } = this.state
        return ( 
        <div className="floatOuterContainer" onMouseMove={ this.mouseMoveHandler.bind(this) }>
            <div className="floatInnerContainer">
                <h1>mouse coordinates: { x }, { y }</h1>
            </div>
        </div>
         );
    }
}

export default FloatingAniamtion;
