import axios from "axios";
import React, { useEffect } from "react";
import { useHistory } from "react-router-dom";
import { store } from "../index"
import { checkUserAuth, createMcConfig } from "../redux/actions/index"
import ReactTooltip from 'react-tooltip';

const SignupPage = () => {

  const history = useHistory();

  useEffect(() => {
    if (store.getState().auth) {
      history.push("/user/home");
      window.location.reload();
    }
  }, [])

  const postReq = (e) => {
    console.log("Name: ", e.target.name.value);
    console.log("Email: ", e.target.email.value);
    console.log("Password: ", e.target.password.value);

    axios.post("/user/insert", {
      data: {
        name: e.target.name.value,
        email: e.target.email.value,
        password: e.target.password.value,
      },
    })
      .then(async (response) => {
        console.log(response.data)
        if (response.data === "User created") {
          store.dispatch({
            type: "MESSAGE",
            payload: response.data
          })
          await store.dispatch(checkUserAuth)
          console.log("1", store.getState().auth)
          await axios.post("/user/login", {
            withCredentials: true,
            name: e.target.name.value,
            password: e.target.password.value
          }).then(async (response) => {
            if (response.data.message === "Success!") {
              document.cookie = `loginAuth=${response.data.loginAuth};path=/`
              document.cookie = `userID=${response.data.userID};path=/`
              await store.dispatch(checkUserAuth)
              create()
              history.push("/user/home");
              window.location.reload();
            } else {
              console.log(response.data.message)
              store.dispatch({
                type: "ERR_MESSAGE",
                payload: response.data.message
              })
            }
          })
        }
        store.dispatch({
          type: "ERR_MESSAGE",
          payload: response.data
        })
      })
    e.preventDefault();
    //history.push("/user/login");
    //window.location.reload();
  };

  const create = () => {
    store.dispatch(createMcConfig)
  }

  const toLogin = () => {
    history.push("/user/login");
    window.location.reload();
  }

  return (
    <div className="signupMainBody">
      <div className="SignupBody">
        <div className="signUpContainerHeader">
          <span>SIGN UP</span>
        </div>
        <form onSubmit={postReq} className="loginCenterInnerContainer">
          <div className="signupForm signupFormName">
            <input
              type="text"
              name="name"
              className="loginInput"
              autoComplete="off"
              required
            />
            <label className="loginLable">
              <span className="loginLableValue">Name</span>
            </label>
          </div>
          <div className="signupForm signupFormEmail">
            <input
              type="text"
              name="email"
              className="loginInput"
              autoComplete="off"
              required
            />
            <label className="loginLable">
              <span className="loginLableValue">Email</span>
            </label>
          </div>
          <div className="signupForm signupFormPassword">
            <input
              type="password"
              name="password"
              className="loginInput"
              autoComplete="off"
              required
            />
            <label className="loginLable">
              <span className="loginLableValue">Password</span>
            </label>
          </div>
          <button type="submit" className="signupForm signupButton">
            submit
          </button>
          <span onClick={toLogin}>Already signed up?</span>
        </form>
      </div>
    </div>
  );
};

export default SignupPage;
