import axios from "axios"
const getEmail = (state = "none",action)=>{

    if(action.type === "EMAIL"){
        return axios.get("http://192.168.1.247:3001/api/user/home", {
            headers: {
                //"customHeader": "test"
            }
        })
        .then(res=>{
            return res.data.data
        })
    }else{
        return state
    }
}

export default getEmail
