import React, { Component, useState, useEffect, useRef } from 'react';
import { Redirect } from "react-router-dom"
import axios from "axios"
import { useAsync } from "react-async"

import SideMenu from "../components/userHome/sideMenu";
import Server from "../components/userHome/server"
import Options from "../components/userHome/options"

import CheckUserAuth from "../auth/checkAuth"

import { store } from "../index"
import { fetchUserData, checkUserAuth } from "../redux/actions/index"

const UserHomePage = (more, loadMore)=> {

    const [page, setPage] = useState(["server"])
    const [userData, setUserData] = useState([store.getState()])
    const componentIsMounted = useRef(true)

    // useAsync(store.dispatch(fetchUserData))
    // useAsync(store.dispatch(checkUserAuth))

    // console.log("1",store.getState())

    useEffect(()=>{
        return()=>{
            componentIsMounted.current = false;
        }
    }, [])

    useEffect(()=>{
        const getAuth = async()=>{
            const cookieValue = document.cookie
            .split('; ')
            .find(row => row.startsWith('loginAuth='))
            .split('=')[1];

            axios.get("http://192.168.1.247:3001/api/user/auth", { 
                withCredentials: true,
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json',
                    "Authorization": cookieValue
                }
            })
            .then(response=>{
                console.log("req success!")
                setUserData(response)
            })
            .catch(err => console.log(err))    
        }
        getAuth()
    }, [more])

    const changeState = (data)=>{
        setPage(data)
    }

    const getPage = ()=>{
         let component
         switch (page){
            case "server":
                component = <Server userData={userData} />
                break;
            case "options":
                component = <Options userData={userData} />
                break;
            default:
                component = <Server userData={userData} />
                break;
            }
        return component
    }

    const authSuccess = ()=>{
        return(
            <dir className="userHomeLayout">
                <dir className="userHomesideMenu">
                    <SideMenu setState={changeState}/>
                </dir>
                {getPage()}
            </dir> 
        )
    }

    const authFaliure = ()=>{
        return(
            <Redirect to="/user/login"/>
        )
    }

        console.log("userdata: ", userData[0].auth)
        switch (userData.auth) {
            case true:
                return authSuccess()
            default:
                return authFaliure()
        }
}

export default UserHomePage;




