import React, { useState, useEffect } from "react";
import { useHistory } from "react-router-dom";

import SideMenu from "../components/userHome/sideMenu";
import Server from "../components/userHome/server";
import Options from "../components/userHome/options";

import { store } from "../index";

const UserHomePage = () => {
  const history = useHistory();
  const [userData, setUserData] = useState([store.getState()]);

  const [didMount, setDidMount] = useState(false);

  useEffect(() => {
    // //autorefresh logs
    // if (store.getState().serverPods.status != "server not running") {
    //   setTimeout(() => {
    //     store.dispatch(serverPodsInfo)
    //     console.log("hello!")
    //     setRemount(!remount)
    //   }, 50000);
    // }

    //adding userHomeData to redux store
    store.dispatch({
      type: "USER_HOME_DATA",
      payload: {
        serverIndex: 0,
        sideMenuSelectedTab: "server",
        showServerLandingPage: true
      }
    })

    //checking if user is logged in, if not, send to home page
    if (store.getState().auth !== null && !store.getState().auth) {
      console.log("Not logged in");
      history.push("/");
      window.location.reload();
    }

    setDidMount(true);
    return () => setDidMount(false);
  }, [])

  if (!didMount) {
    return null;
  }

  useEffect(() => {
    const unsubscribe = store.subscribe(() => {
      updateUserData(store.getState());
    });
    if (store.getState().userHomeData.sideMenuTab !== "server") unsubscribe()
  })

  const getPage = () => {
    let component;
    switch (store.getState().userHomeData.sideMenuSelectedTab) {
      case "server":
        component = <Server userData={userData} />;
        break;
      case "options":
        component = <Options userData={userData} />;
        break;
      default:
        component = <Server userData={userData} />;
        break;
    }
    return component;
  };

  //auth();
  return (
    <dir className="userHomeLayout" id="random03242jcmvmj0v23cm4">
      <dir className="userHomesideMenu">
        <SideMenu />
      </dir>
      {getPage()}
    </dir>
  );
};

export default UserHomePage;
