import { combineReducers } from "redux"

const initailState = {
    user: "default",
    auth: null,
    serverPods: "default",
    serverSVC: "default"
}

const reducer = (state = initailState, action) => {

    switch (action.type) {
        case "FETCH_USER_SUCCESS" || "AUTH_SUCCESS" || "SERVER_PODS_DATA" || "SERVER_SVC_DATA" || MC_CONF_GET_DATA:
            return {
                ...state,
                user: action.payload
            }
        case "DUMP":
            return {
                ...state,
            }
        default:
            return {
                ...state
            }
    }
}

export default reducer
