const FloatingAniamtion = ()=> {

    const mouseMoveHandler =(e)=>{
        const x = e.screenX 
        const y = e.screenY
        axis()
    } 

    const axis = ()=>{
        const container = document.querySelector(".floatOuterContainer")

        const xAxis = (x - container.offsetWidth /2) /50
        const yAxis = (container.offsetHeight /2 - y) /50
        animation()
    }

    const animation = ()=>{
        const object = document.querySelector(".floatInnerContainer")
        object.style.transform = `rotateY(${xAxis}deg) rotateX(${yAxis}deg)`
    }   

    return ( 
    <div className="floatOuterContainer" onMouseMove={ mouseMoveHandler.bind(this) }>
        <div className="floatInnerContainer">
            {}
        </div>
    </div>
    );
}

export default FloatingAniamtion;
